import React, { useEffect, useState } from "react";
import axios from "axios";

const UserCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const user_id = localStorage.getItem("userID");

  useEffect(() => {
    axios
      .get(`http://localhost:3001/api/getcartitems/${user_id}`)
      .then((res) => setCartItems(res.data))
      .catch((err) => console.error(err))
      .finally(() => setLoading(false));
  }, [user_id]);

  const handleQuantityChange = (id, qty) => {
    if (qty < 1) return;

    axios
      .put(`http://localhost:3001/api/updatecart/${id}`, { qty })
      .then(() => {
        setCartItems((prev) =>
          prev.map((item) =>
            item.id === id
              ? { ...item, qty: Number(qty), total: item.price * qty }
              : item
          )
        );
      })
      .catch(() => alert("Failed to update quantity"));
  };

  const handleRemove = (id) => {
    axios
      .delete(`http://localhost:3001/api/deletefromcart/${id}`)
      .then(() => {
        window.dispatchEvent(new Event("cart-update"));
        setCartItems((prev) => prev.filter((i) => i.id !== id));
      })
      .catch(() => alert("Failed to remove item"));
  };

  const totalPrice = cartItems.reduce((sum, item) => sum + item.total, 0);

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center py-5">
        <div className="spinner-border text-primary" />
        <span className="ms-2">Loading cart...</span>
      </div>
    );
  }

  return (
    <section className="py-4">
      <h2 className="fw-bold text-center mb-4">My Cart</h2>

      {cartItems.length === 0 ? (
        <p className="text-center text-muted fs-5">
          Your cart is empty.
        </p>
      ) : (
        <>
          <div className="d-flex flex-column gap-3">
            {cartItems.map((item) => (
              <div key={item.id} className="card p-3">
                <div className="row align-items-center">
                  <div className="col-md-1 text-center">
                    <img
                      src={`http://localhost:3001/uploads/${item.image}`}
                      alt={item.product_name}
                      style={{ width: 60, height: 60, objectFit: "cover" }}
                      className="rounded"
                    />
                  </div>

                  <div className="col-md-4">
                    <h6 className="fw-semibold mb-1">
                      {item.product_name}
                    </h6>
                    <small className="text-muted">
                      ₹{item.price.toFixed(2)}
                    </small>
                  </div>

                  <div className="col-md-3">
                    <input
                      type="number"
                      min="1"
                      value={item.qty}
                      onChange={(e) =>
                        handleQuantityChange(item.id, e.target.value)
                      }
                      className="form-control form-control-sm"
                      style={{ maxWidth: 90 }}
                    />
                  </div>

                  <div className="col-md-2 fw-bold text-success">
                    ₹{(item.price * item.qty).toFixed(2)}
                  </div>

                  <div className="col-md-2 text-end">
                    <button
                      className="btn btn-outline-danger btn-sm"
                      onClick={() => handleRemove(item.id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="card mt-4 p-3">
            <div className="d-flex justify-content-between align-items-center">
              <h5 className="fw-bold mb-0">
                Total:{" "}
                <span className="text-success">
                  ₹{totalPrice.toFixed(2)}
                </span>
              </h5>

              <button
                className="btn btn-success px-4"
                onClick={() =>
                  (window.location.href = `/paybill?uid=${user_id}&price=${totalPrice.toFixed(
                    2
                  )}`)
                }
              >
                Checkout
              </button>
            </div>
          </div>
        </>
      )}
    </section>
  );
};

export default UserCart;
