import React, { useEffect, useState } from 'react';

const RegistrationView = () => {
    const [RegData, setRegData] = useState([]);

    useEffect(() => {
        fetch('http://localhost:3001/api/getregister')
            .then(response => response.json())
            .then(data => setRegData(data))
            .catch(error => console.error('Error fetching registration data:', error));
    }, []);

    const DeleteReg = async (id) => {
        try {
            const response = await fetch(`http://localhost:3001/api/deleteregister/${id}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                setRegData(RegData.filter(user => user.id !== id));
            } else {
                console.error('Failed to delete the user.');
            }
        } catch (error) {
            console.error('Error deleting the user:', error);
        }
    }

    return (
        <div className="container my-5">
            <div className="text-center">
                <h1 className="mb-4">Registered Users</h1>
                <table className="table table-bordered">
                    <thead className="table-dark">
                        <tr>
                            <th>Name</th>
                            <th>Gender</th>
                            <th>City</th>
                            <th>Address</th>
                            <th>Pincode</th>
                            <th>Email</th>
                            <th> Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {RegData.map((user, index) => (
                            <tr key={index}>
                                <td>{user.name}</td>
                                <td>{user.gender}</td>
                                <td>{user.city}</td>
                                <td>{user.address}</td>
                                <td>{user.pincode}</td>
                                <td>{user.email}</td>
                                <td> <i className='fa fa-trash text-danger' onClick={() => { if (window.confirm('Are you sure you want to delete this user?')) DeleteReg(user.id); }}>Delete</i></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default RegistrationView;
