import React, { useState, useEffect } from 'react';
import axios from "axios";
import { useLocation } from "react-router-dom";

const FeedbackPage = () => {
    const [formData, setFormData] = useState({
        product_id: '',
        aboutProduct: '',
        aboutService: '',
        comments: '',
        rating: 0
    });

    const [hoverRating, setHoverRating] = useState(0); // â­ for hover effect
    const location = useLocation();

    // ðŸ”¹ Fetch product_id from query params like /feedback?product_id=12
    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const product_id = queryParams.get("product_id");
        if (product_id) {
            setFormData((prev) => ({ ...prev, product_id }));
        }
    }, [location]);

    // ðŸ”¹ Handle input change
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    // ðŸ”¹ Submit feedback
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate rating
        if (formData.rating < 1 || formData.rating > 5) {
            alert("Please select a valid star rating between 1 and 5!");
            return;
        }

        try {
            console.log("Feedback Submitted:", formData);

            const response = await axios.post("http://localhost:3001/api/feedback", formData);

            if (response.status === 200) {
                alert("Feedback submitted successfully!");
                // Reset form
                setFormData({
                    product_id: '',
                    aboutProduct: '',
                    aboutService: '',
                    comments: '',
                    rating: 0
                });
                setHoverRating(0);
            } else {
                alert("Failed to submit feedback. Please try again.");
            }
        } catch (error) {
            console.error("Error submitting feedback:", error);
            alert("An error occurred during the process. Please try again later.");
        }
    };

    return (
        <div className="d-flex justify-content-center py-5 bg-light">
            <div className="card shadow p-4" style={{ width: "100%", maxWidth: "450px" }}>
                <h3 className="text-center mb-4 text-primary fw-bold">Feedback Form</h3>

                <form onSubmit={handleSubmit}>
                    {/* Star Rating */}
                    <div className="mb-3 text-center">
                        <label className="form-label d-block fw-semibold">Rate the Product</label>
                        {[1, 2, 3, 4, 5].map((star) => (
                            <i
                                key={star}
                                className={`bi ${star <= (hoverRating || formData.rating)
                                        ? "bi-star-fill text-warning"
                                        : "bi-star"
                                    } fs-4 mx-1`}
                                style={{ cursor: "pointer", transition: "color 0.2s" }}
                                onMouseEnter={() => setHoverRating(star)}
                                onMouseLeave={() => setHoverRating(0)}
                                onClick={() => {
                                    if (star >= 1 && star <= 5) {
                                        setFormData({ ...formData, rating: star });
                                    }
                                }}
                            ></i>
                        ))}
                    </div>

                    {/* About Product Field */}
                    <div className="mb-3">
                        <label htmlFor="aboutProduct" className="form-label">About Product</label>
                        <input
                            type="text"
                            className="form-control"
                            id="aboutProduct"
                            name="aboutProduct"
                            placeholder="What do you think about our product?"
                            required
                            value={formData.aboutProduct}
                            onChange={handleChange}
                        />
                    </div>

                    {/* About Service Field */}
                    <div className="mb-3">
                        <label htmlFor="aboutService" className="form-label">About Service</label>
                        <input
                            type="text"
                            className="form-control"
                            id="aboutService"
                            name="aboutService"
                            placeholder="Share your thoughts on our service"
                            required
                            value={formData.aboutService}
                            onChange={handleChange}
                        />
                    </div>

                    {/* Comments Field */}
                    <div className="mb-3">
                        <label htmlFor="comments" className="form-label">Additional Comments</label>
                        <textarea
                            className="form-control"
                            id="comments"
                            name="comments"
                            rows="3"
                            placeholder="Any suggestions or feedback?"
                            required
                            value={formData.comments}
                            onChange={handleChange}
                        ></textarea>
                    </div>

                    {/* Submit Feedback Button */}
                    <button type="submit" className="btn btn-primary w-100">
                        Submit Feedback
                    </button>
                </form>
            </div>
        </div>
    );
};

export default FeedbackPage;
