import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalSales: 0,
    totalOrders: 0,
    totalCustomers: 0,
    todayOrders: 0,
  });
  const [recentOrders, setRecentOrders] = useState([]);
  const [recentReviews, setRecentReviews] = useState([]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [salesRes, ordersRes, usersRes, todayOrdersRes] = await Promise.all([
          axios.get("http://localhost:3001/api/totalsales"),
          axios.get("http://localhost:3001/api/totalorders"),
          axios.get("http://localhost:3001/api/totalcustomers"),
          axios.get("http://localhost:3001/api/todayorders"),
        ]);

        setStats({
          totalSales: salesRes.data.totalSales || 0,
          totalOrders: ordersRes.data.totalOrders || 0,
          totalCustomers: usersRes.data.totalCustomers || 0,
          todayOrders: todayOrdersRes.data.todayOrders || 0,
        });
      } catch (error) {
        console.error("Error fetching dashboard stats:", error);
      }
    };

    const fetchRecentOrders = async () => {
      try {
        const response = await axios.get("http://localhost:3001/api/recentorders");
        if (response.status === 200) setRecentOrders(response.data.orders);
      } catch (error) {
        console.error("Error fetching recent orders:", error);
      }
    };

    const fetchRecentReviews = async () => {
      try {
        const response = await axios.get("http://localhost:3001/api/recentreviews");
        if (response.status === 200) setRecentReviews(response.data.reviews);
      } catch (error) {
        console.error("Error fetching recent reviews:", error);
      }
    };

    fetchStats();
    fetchRecentOrders();
    fetchRecentReviews();
  }, []);

  return (
    <div className="container-fluid">
      <div className="row">
        {/* Sidebar */}
        <div className="col-md-3 col-lg-2 bg-primary text-white vh-100 p-4 d-flex flex-column sticky-top">
          <h4 className="fw-bold mb-4 text-center">Dashboard</h4>
          <ul className="nav flex-column">
            <li className="nav-item mb-2">
              <a href="/adminorders" className="nav-link text-white">
                <i className="bi bi-box-seam me-2"></i> Orders
              </a>
            </li>
            <li className="nav-item mb-2">
              <a href="/feedbackview" className="nav-link text-white">
                <i className="bi bi-chat-dots me-2"></i> Reviews
              </a>
            </li>
            <li className="nav-item mb-2">
