import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import axios from "axios";

const ProductAddPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    categoryName: '',
    productName: '',
    quantity: '',
    uom: '',
    price: '',
    stock: '',
    image: null,
    description: ''
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: files ? files[0] : value, // handle file separately
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Prepare form data for upload (important for images)
      const formDataToSend = new FormData();
      Object.entries(formData).forEach(([key, value]) => {
        formDataToSend.append(key, value);
      });

      const response = await axios.post(
        "http://localhost:3001/api/addProduct",
        formDataToSend,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );

      if (response.status === 200) {
        alert("✅ Product added successfully!");
        setFormData({
          categoryName: '',
          productName: '',
          quantity: '',
          uom: '',
          price: '',
          stock: '',
          image: null,
          description: '',
        });
        navigate("/productview");
      } else {
        alert("⚠️ Failed to add product. Please try again.");
      }
    } catch (error) {
      console.error("❌ Error adding product:", error);
      alert("An error occurred during the process. Please try again later.");
    }
  };

  return (
    <div className="d-flex justify-content-center py-5 bg-light">
      <div className="card shadow p-4" style={{ width: "100%", maxWidth: "500px" }}>
        <h3 className="text-center mb-4 text-primary fw-bold">Add Product</h3>

        <form onSubmit={handleSubmit} encType="multipart/form-data">
          {/* Category Name */}
          <div className="mb-3">
            <label htmlFor="categoryName" className="form-label">
              Category Name
            </label>
            <input
              type="text"
              className="form-control"
              id="categoryName"
              name="categoryName"
              placeholder="Enter category name"
              required
              value={formData.categoryName}
              onChange={handleChange}
            />
          </div>

          {/* Product Name */}
          <div className="mb-3">
            <label htmlFor="productName" className="form-label">
              Product Name
            </label>
            <input
              type="text"
              className="form-control"
              id="productName"
              name="productName"
              placeholder="Enter product name"
              required
              value={formData.productName}
              onChange={handleChange}
            />
          </div>

          {/* Quantity */}
          <div className="mb-3">
            <label htmlFor="quantity" className="form-label">
              Quantity
            </label>
            <input
              type="number"
              className="form-control"
              id="quantity"
              name="quantity"
              min="0"
              required
              value={formData.quantity}
              onChange={handleChange}
            />
          </div>

          {/* Unit of Measure */}
          <div className="mb-3">
            <label htmlFor="uom" className="form-label">
              Unit of Measure (UOM)
            </label>
            <input
              type="text"
              className="form-control"
              id="uom"
              name="uom"
              placeholder="e.g., kg, liter, pcs"
              required
              value={formData.uom}
              onChange={handleChange}
            />
          </div>

          {/* Price */}
          <div className="mb-3">
            <label htmlFor="price" className="form-label">
              Price (₹)
            </label>
            <input
              type="number"
              className="form-control"
              id="price"
              name="price"
              min="0"
              step="0.01"
              required
              value={formData.price}
              onChange={handleChange}
            />
          </div>

          {/* Stock */}
          <div className="mb-3">
            <label htmlFor="stock" className="form-label">
              Stock
            </label>
            <input
              type="number"
              className="form-control"
              id="stock"
              name="stock"
              min="0"
              required
              value={formData.stock}
              onChange={handleChange}
            />
          </div>

          {/* Image Upload */}
          <div className="mb-3">
            <label htmlFor="image" className="form-label">
              Product Image
            </label>
            <input
              type="file"
              className="form-control"
              id="image"
              name="image"
              accept="image/*"
              onChange={handleChange}
            />
          </div>

          {/* Description */}
          <div className="mb-3">
            <label htmlFor="description" className="form-label">
              Description
            </label>
            <textarea
              className="form-control"
              id="description"
              name="description"
              rows="3"
              placeholder="Enter product description"
              value={formData.description}
              onChange={handleChange}
            ></textarea>
          </div>

          {/* Submit Button */}
          <button type="submit" className="btn btn-primary w-100">
            Add Product
          </button>
        </form>
      </div>
    </div>
  );
};

export default ProductAddPage;
