import React, { useState } from 'react'

const OtpScreen = () => {
    const [otp, setOtp] = useState('');

    const handleChange = (e) => {
        setOtp(e.target.value);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Submitted OTP:", otp);

        const url = "http://localhost:3001/api/verifyotp";
        fetch(url, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                otp: otp
            })
        }).then((res) => res.json())
            .then((data) => {
                console.log(data);
                if (data.success) {
                    alert("OTP Verified Successfully!");
                    // Redirect to reset password page
                    window.location.href = "/resetpassword";
                } else {
                    alert(data.message);
                }
            });
    }

    return (
        <div className="container my-5">
            <h2 className="mb-4 text-center text-primary fw-bold">OTP Verification</h2>
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="otp" className="form-label">Enter OTP</label>
                            <input type="text" onChange={handleChange} className="form-control" id="otp" placeholder="Enter the OTP sent to your email" />
                        </div>
                        <button type="submit" className="btn btn-primary w-100">Verify OTP</button>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default OtpScreen
