import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminHome = () => {
  const [stats, setStats] = useState({
    totalSales: 0,
    totalOrders: 0,
    totalCustomers: 0,
  });

  useEffect(() => {
    // Example API calls â€” replace these endpoints with your backend routes
    const fetchStats = async () => {
      try {
        const [salesRes, ordersRes, usersRes] = await Promise.all([
          axios.get("http://localhost:3001/api/totalsales"),
          axios.get("http://localhost:3001/api/totalorders"),
          axios.get("http://localhost:3001/api/totalcustomers"),
        ]);

        setStats({
          totalSales: salesRes.data.totalSales || 0,
          totalOrders: ordersRes.data.totalOrders || 0,
          totalCustomers: usersRes.data.totalCustomers || 0,
        });
      } catch (error) {
        console.error("Error fetching dashboard stats:", error);
      }
    };

    fetchStats();
  }, []);

  return (
    <div className="container-fluid">
      <div className="row">
        {/* Sidebar */}
        <div className="col-md-3 col-lg-2 bg-primary text-white vh-100 p-4 d-flex flex-column sticky-top">
          <h4 className="fw-bold mb-4 text-center">Dashboard</h4>
          <ul className="nav flex-column">
            <li className="nav-item mb-2">
              <a href="/adminorders" className="nav-link text-white">
                <i className="bi bi-box-seam me-2"></i> Orders
              </a>
            </li>
            <li className="nav-item mb-2">
              <a href="/feedbackview" className="nav-link text-white">
                <i className="bi bi-chat-dots me-2"></i> Reviews
              </a>
            </li>
            <li className="nav-item mb-2">
              <a href="/productview" className="nav-link text-white">
                <i className="bi bi-bag me-2"></i> Products
              </a>
            </li>
            <li className="nav-item mb-2 dropdown">
              <a
                href=""
                className="nav-link dropdown-toggle text-white"
                data-bs-toggle="dropdown"
              >
                <i className="bi bi-bar-chart-line me-2"></i> Reports
              </a>
              <ul className="dropdown-menu">
                <li>
                  <a className="dropdown-item" href="/reports/yearly">
                    Yearly Sales
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="/reports/monthly">
                    Monthly Sales
                  </a>
                </li>
              </ul>
            </li>
            <li className="nav-item mt-auto">
              <button
                className="btn btn-outline-light w-100 mt-3"
                onClick={() => {
                  localStorage.clear();
                  window.location.href = "/";
                }}
              >
                <i className="bi bi-box-arrow-right me-2"></i> Logout
              </button>
            </li>
          </ul>
        </div>

        {/* Main Content */}
        <div className="col-md-9 col-lg-10 p-4">
          <h2 className="fw-bold text-primary mb-4">Welcome, Admin ðŸ‘‹</h2>

          {/* Cards Row */}
          <div className="row g-4">
            {/* Total Sales */}
            <div className="col-md-4">
              <div className="card shadow-sm border-0 rounded-4 p-3 text-center h-100">
                <div className="card-body">
                  <div className="text-muted small mb-2">Total Sales</div>
                  <h3 className="fw-bold text-success">
                    â‚¹{stats.totalSales.toLocaleString()}
                  </h3>
                  <i className="bi bi-cash-coin text-success fs-2 mt-2"></i>
                </div>
              </div>
            </div>

            {/* Total Orders */}
            <div className="col-md-4">
              <div className="card shadow-sm border-0 rounded-4 p-3 text-center h-100">
                <div className="card-body">
                  <div className="text-muted small mb-2">Total Orders</div>
                  <h3 className="fw-bold text-primary">{stats.totalOrders}</h3>
                  <i className="bi bi-cart-check text-primary fs-2 mt-2"></i>
                </div>
              </div>
            </div>

            {/* Total Customers */}
            <div className="col-md-4">
              <div className="card shadow-sm border-0 rounded-4 p-3 text-center h-100">
                <div className="card-body">
                  <div className="text-muted small mb-2">Total Customers</div>
                  <h3 className="fw-bold text-warning">{stats.totalCustomers}</h3>
                  <i className="bi bi-people text-warning fs-2 mt-2"></i>
                </div>
              </div>
            </div>
          </div>

          {/* Placeholder for charts or reports */}
          <div className="mt-5">
            <h5 className="fw-semibold mb-3">Recent Reports</h5>
            <div className="p-4 bg-light rounded-4 text-muted text-center">
              ðŸ“Š Reports and analytics section coming soon.
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminHome;
