import React, { useEffect, useState } from "react";
import axios from "axios";

const UserOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const user_id = localStorage.getItem("userID");

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get(
          `http://localhost:3001/api/userorders/${user_id}`
        );
        if (res.status === 200) {
          setOrders(res.data.orders);
        } else {
          setError("Failed to fetch orders.");
        }
      } catch (err) {
        setError("An error occurred while fetching your orders.");
      } finally {
        setLoading(false);
      }
    };

    if (user_id) fetchOrders();
  }, [user_id]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center py-5">
        <div className="spinner-border text-primary"></div>
        <span className="ms-2">Loading orders...</span>
      </div>
    );
  }

  if (error) {
    return <p className="text-center text-danger mt-5">{error}</p>;
  }

  const handlePayment = (amount) => {
    window.location.href = `/paybill?uid=${user_id}&price=${amount}`;
  };

  const handleReview = (productId) => {
    window.location.href = `/feedback?product_id=${productId}`;
  };

  return (
    <section className="py-5">
      <h2 className="fw-bold text-center mb-4">My Orders</h2>

      {orders.length === 0 ? (
        <p className="text-center text-muted fs-5">
          You haven’t placed any orders yet.
        </p>
      ) : (
        <div className="d-flex flex-column gap-4">
          {orders.map((order) => {
            const isUnpaid = order.paymentStatus === "Unpaid";

            return (
              <div key={order.id} className="card p-0">
                {/* Header */}
                <div className="card-header bg-white d-flex justify-content-between align-items-center">
                  <h6 className="fw-bold mb-0">Order #{order.id}</h6>
                  <span
                    className={`badge ${order.status === "Confirmed"
                      ? "bg-success"
                      : order.status === "Shipped"
                        ? "bg-info text-dark"
                        : order.status === "Delivered"
                          ? "bg-primary"
                          : "bg-secondary"
                      }`}
                  >
                    {order.status}
                  </span>
                </div>

                {/* Body */}
                <div className="card-body">
                  {order.products.map((product, idx) => (
                    <div
                      key={idx}
                      className="d-flex justify-content-between align-items-center border-bottom pb-2 mb-2"
                    >
                      <div className="d-flex align-items-center">
                        <img
                          src={`http://localhost:3001/uploads/${product.image}`}
                          alt={product.name}
                          className="rounded me-3"
                          style={{ width: "55px", height: "55px", objectFit: "cover" }}
                          onError={(e) =>
                            (e.target.src = "/default-product.png")
                          }
                        />

                        <div>
                          <div className="fw-semibold">
                            {product.name}
                          </div>
                          <small className="text-muted">
                            ₹{product.price} × {product.qty}
                          </small>
                        </div>
                      </div>

                      {!isUnpaid && (
                        <button
                          className="btn btn-outline-primary btn-sm"
                          onClick={() =>
                            handleReview(product.id || product.pid)
                          }
                        >
                          Review
                        </button>
                      )}
                    </div>
                  ))}

                  <div className="d-flex justify-content-between align-items-center mt-3">
                    <div>
                      <small className="text-muted">Payment</small>
                      <span
                        className={`badge ms-2 ${order.paymentStatus === "Paid"
                          ? "bg-success"
                          : "bg-warning text-dark"
                          }`}
                      >
                        {order.paymentStatus}
                      </span>
                    </div>

                    <div className="fw-bold text-success">
                      ₹{order.totalAmount?.toFixed(2)}
                    </div>
                  </div>

                  {isUnpaid && (
                    <div className="text-end mt-3">
                      <button
                        className="btn btn-warning"
                        onClick={() =>
                          handlePayment(order.totalAmount.toFixed(2))
                        }
                      >
                        Pay Now
                      </button>
                    </div>
                  )}
                </div>

                {/* Footer */}
                <div className="card-footer bg-white text-muted small">
                  Ordered on{" "}
                  {new Date(order.orderDate).toLocaleString("en-IN", {
                    dateStyle: "medium",
                    timeStyle: "short",
                  })}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </section>
  );
};

export default UserOrders;
