import React, { useEffect, useState, useCallback } from "react";
import { NavLink, useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const [utype, setUtype] = useState(localStorage.getItem("utype"));
  const [cartCount, setCartCount] = useState(0);
  const isLoggedIn = !!utype;
  const user_id = localStorage.getItem("userID");

  // Fetch cart count
  const fetchCartCount = useCallback(async () => {
    if (utype === "user" && user_id) {
      try {
        const response = await fetch(`http://localhost:3001/api/getcartitems/${user_id}`);
        if (response.ok) {
          const data = await response.json();
          setCartCount(data.length);
        }
      } catch (error) {
        console.error("Error fetching cart count:", error);
      }
    }
  }, [utype, user_id]);

  // Initial fetch + cart-update listener
  useEffect(() => {
    fetchCartCount();
    const handleCartUpdate = () => fetchCartCount();
    window.addEventListener("cart-update", handleCartUpdate);
    return () => window.removeEventListener("cart-update", handleCartUpdate);
  }, [fetchCartCount]);

  // React to storage changes (cross-tab + same-tab)
  useEffect(() => {
    const handleStorageChange = () => setUtype(localStorage.getItem("utype"));
    window.addEventListener("storage", handleStorageChange);
    window.addEventListener("app-storage", handleStorageChange);
    return () => {
      window.removeEventListener("storage", handleStorageChange);
      window.removeEventListener("app-storage", handleStorageChange);
    };
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    window.dispatchEvent(new Event("app-storage"));
    navigate("/");
  };

  const homeLink =
    utype === "admin" ? "/adminhome" : utype === "user" ? "/userhome" : "/";

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-primary sticky-top">
      <div className="container">
        <NavLink className="navbar-brand fw-bold" to={homeLink}>
          E-Commerce Website
        </NavLink>

        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
          <ul className="navbar-nav align-items-lg-center">
            {/* Common Home Link */}
            <li className="nav-item">
              <NavLink to={homeLink} className="nav-link">
                Home
              </NavLink>
            </li>

            {/* ---------------- ADMIN MENU ---------------- */}
            {utype === "admin" && (
              <>
                <li className="nav-item dropdown">
                  <button
                    className="nav-link dropdown-toggle btn btn-link text-decoration-none"
                    type="button"
                    data-bs-toggle="dropdown"
                  >
                    Categories
                  </button>
                  <ul className="dropdown-menu">
                    <li>
                      <NavLink className="dropdown-item" to="/categoryview">
                        View Categories
                      </NavLink>
                    </li>
                    <li>
                      <NavLink className="dropdown-item" to="/category">
                        Add Category
                      </NavLink>
                    </li>
                  </ul>
                </li>

                <li className="nav-item dropdown">
                  <button
                    className="nav-link dropdown-toggle btn btn-link text-decoration-none"
                    type="button"
                    data-bs-toggle="dropdown"
                  >
                    Products
                  </button>
                  <ul className="dropdown-menu">
                    <li>
                      <NavLink className="dropdown-item" to="/productview">
                        View Products
                      </NavLink>
                    </li>
                    <li>
                      <NavLink className="dropdown-item" to="/addproduct">
                        Add Product
                      </NavLink>
                    </li>
                  </ul>
                </li>

                <li className="nav-item">
                  <NavLink to="/orders" className="nav-link">
                    Orders
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/registerview" className="nav-link">
                    Users
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/feedbackview" className="nav-link">
                    Feedbacks
                  </NavLink>
                </li>
                <li className="nav-item">
                  <button
                    onClick={handleLogout}
                    className="btn btn-link nav-link text-light"
                  >
                    Logout
                  </button>
                </li>
              </>
            )}

            {/* ---------------- USER MENU ---------------- */}
            {utype === "user" && (
              <>
                <li className="nav-item">
                  <NavLink to="/userOrders" className="nav-link">
                    My Orders
                  </NavLink>
                </li>

                <li className="nav-item">
                  <NavLink to="/usercart" className="nav-link position-relative">
                    <i className="bi bi-cart3 fs-5 me-1"></i>
                    {cartCount > 0 && (
                      <span className="position-absolute top-20 start-80 translate-middle badge rounded-pill bg-danger">
                        {cartCount}
                        <span className="visually-hidden">Cart items</span>
                      </span>
                    )}
                  </NavLink>
                </li>

                <li className="nav-item">
                  <NavLink to="/aboutus" className="nav-link">
                    About Us
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/contactus" className="nav-link">
                    Contact Us
                  </NavLink>
                </li>

                <li className="nav-item">
                  <button
                    onClick={handleLogout}
                    className="btn btn-link nav-link text-light"
                  >
                    Logout
                  </button>
                </li>
              </>
            )}

            {/* ---------------- GUEST MENU ---------------- */}
            {!isLoggedIn && (
              <>
                <li className="nav-item">
                  <NavLink to="/products" className="nav-link">
                    Products
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/aboutus" className="nav-link">
                    About Us
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/contactus" className="nav-link">
                    Contact Us
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/login" className="nav-link">
                    Login
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/register" className="nav-link fw-semibold text-warning">
                    Sign Up
                  </NavLink>
                </li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
