import { NavLink, useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const [utype, setUtype] = useState(localStorage.getItem("utype"));
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("token"));

  // ðŸ§  Re-run whenever localStorage changes (auto update navbar)
  useEffect(() => {
    const handleStorageChange = () => {
      setUtype(localStorage.getItem("utype"));
      setIsLoggedIn(!!localStorage.getItem("token"));
    };

    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    setIsLoggedIn(false);
    setUtype(null);
    navigate("/");
  };

  const homeLink =
    utype === "admin" ? "/adminhome" : utype === "user" ? "/userhome" : "/";

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-primary sticky-top shadow-sm">
      <div className="container">
        <NavLink className="navbar-brand fw-bold" to={homeLink}>
          E-Commerce Website
        </NavLink>

        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <NavLink to={homeLink} className="nav-link">
                Home
              </NavLink>
            </li>

            {/* âœ… Admin Navbar */}
            {utype === "admin" && (
              <>
                <li className="nav-item dropdown">
                  <button
                    className="nav-link dropdown-toggle btn btn-link text-decoration-none"
                    type="button"
                    data-bs-toggle="dropdown"
                  >
                    Category
                  </button>
                  <ul className="dropdown-menu">
                    <li>
                      <NavLink className="dropdown-item" to="/categoryview">
                        View Categories
                      </NavLink>
                    </li>
                    <li>
                      <NavLink className="dropdown-item" to="/category">
                        Add Category
                      </NavLink>
                    </li>
                  </ul>
                </li>

                <li className="nav-item dropdown">
                  <button
                    className="nav-link dropdown-toggle btn btn-link text-decoration-none"
                    type="button"
                    data-bs-toggle="dropdown"
                  >
                    Products
                  </button>
                  <ul className="dropdown-menu">
                    <li>
                      <NavLink className="dropdown-item" to="/productview">
                        View Products
                      </NavLink>
                    </li>
                    <li>
                      <NavLink className="dropdown-item" to="/addproduct">
                        Add Product
                      </NavLink>
                    </li>
                  </ul>
                </li>

                <li className="nav-item">
                  <NavLink to="/orders" className="nav-link">
                    Orders
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/registerview" className="nav-link">
                    Users
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/feedbackview" className="nav-link">
                    Feedbacks
                  </NavLink>
                </li>
              </>
            )}

            {/* âœ… User Navbar */}
            {utype === "user" && (
              <>
                <li className="nav-item dropdown">
                  <button
                    className="nav-link dropdown-toggle btn btn-link text-decoration-none"
                    type="button"
                    data-bs-toggle="dropdown"
                  >
                    Categories
                  </button>
                  <ul className="dropdown-menu">
                    <li>
                      <NavLink className="dropdown-item" to="/categoryview">
                        Laptops
                      </NavLink>
                    </li>
                    <li>
                      <NavLink className="dropdown-item" to="/categoryview">
                        TVs
                      </NavLink>
                    </li>
                    <li>
                      <NavLink className="dropdown-item" to="/categoryview">
                        Washing Machines
                      </NavLink>
                    </li>
                  </ul>
                </li>

                <li className="nav-item">
                  <NavLink to="/orders" className="nav-link">
                    My Orders
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/usercart" className="nav-link">
                    My Cart
                  </NavLink>
                </li>
              </>
            )}

            {/* âœ… Guest Navbar */}
            {!isLoggedIn && (
              <>
                <li className="nav-item">
                  <NavLink to="/aboutus" className="nav-link">
                    About Us
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/contactus" className="nav-link">
                    Contact Us
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/login" className="nav-link">
                    Login
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink to="/register" className="nav-link">
                    Sign Up
                  </NavLink>
                </li>
              </>
            )}

            {/* âœ… Logout Button */}
            {isLoggedIn && (
              <li className="nav-item">
                <button
                  onClick={handleLogout}
                  className="btn btn-link nav-link text-light"
                >
                  Logout
                </button>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
